mapper.update:
UPDATE device_alarm SET update_time = #{updateTime} 

    ,id = #{id}


    ,sid = #{sid}


    ,device_id = #{deviceId}


    ,alarm_information = #{alarmInformation}


    ,alarm_time = #{alarmTime}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND id = #{id}
    
    
        AND sid = #{sid}
    
    
        AND device_id = #{deviceId}
    
    
        AND alarm_information = #{alarmInformation}
    
    
        AND alarm_time = #{alarmTime}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

mapper.select:
SELECT  FROM device_alarm
WHERE 1 = 1 

    AND id = #{id}


    AND sid = #{sid}


    AND device_id = #{deviceId}


    AND alarm_information = #{alarmInformation}


    AND alarm_time = #{alarmTime}


    AND is_use = #{isUse}


    AND create_time = #{createTime}


    AND creator = #{creator}


    AND update_time = #{updateTime}


    AND updator = #{updator}

mapper.update:
