mapper.update:
UPDATE mec_item SET update_time = #{updateTime} 

    ,mec_item_id = #{mecItemId}


    ,sid = #{sid}


    ,mec_item_name = #{mecItemName}


    ,mec_item_explain = #{mecItemExplain}


    ,rule_num = #{ruleNum}


    ,initial_score = #{initialScore}


    ,interval_score = #{intervalScore}


    ,auth = #{auth}


    ,tids = #{tids}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND mec_item_id = #{mecItemId}
    
    
        AND sid = #{sid}
    
    
        AND mec_item_name = #{mecItemName}
    
    
        AND mec_item_explain = #{mecItemExplain}
    
    
        AND rule_num = #{ruleNum}
    
    
        AND initial_score = #{initialScore}
    
    
        AND interval_score = #{intervalScore}
    
    
        AND auth = #{auth}
    
    
        AND tids = #{tids}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

mapper.select:
