mapper.update:
UPDATE news SET update_time = #{updateTime} 

    ,news_id = #{newsId}


    ,sid = #{sid}


    ,column_id = #{columnId}


    ,title = #{title}


    ,content = #{content}


    ,is_top = #{isTop}


    ,is_pic = #{isPic}


    ,pic = #{pic}


    ,news_type = #{newsType}


    ,news_reader = #{newsReader}


    ,news_classer = #{newsClasser}


    ,publish_time = #{publishTime}


    ,is_audit = #{isAudit}


    ,num = #{num}


    ,author = #{author}


    ,is_homenews = #{isHomenews}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND news_id = #{newsId}
    
    
        AND sid = #{sid}
    
    
        AND column_id = #{columnId}
    
    
        AND title = #{title}
    
    
        AND content = #{content}
    
    
        AND is_top = #{isTop}
    
    
        AND is_pic = #{isPic}
    
    
        AND pic = #{pic}
    
    
        AND news_type = #{newsType}
    
    
        AND news_reader = #{newsReader}
    
    
        AND news_classer = #{newsClasser}
    
    
        AND publish_time = #{publishTime}
    
    
        AND is_audit = #{isAudit}
    
    
        AND num = #{num}
    
    
        AND author = #{author}
    
    
        AND is_homenews = #{isHomenews}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

mapper.select:
