mapper.update:
UPDATE oa_tags SET update_time = #{updateTime} 

    ,oatags_id = #{oatagsId}


    ,sid = #{sid}


    ,oatags_name = #{oatagsName}


    ,type = #{type}


    ,cycle = #{cycle}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND oatags_id = #{oatagsId}
    
    
        AND sid = #{sid}
    
    
        AND oatags_name = #{oatagsName}
    
    
        AND type = #{type}
    
    
        AND cycle = #{cycle}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

