mapper.update:
UPDATE page_personal SET update_time = #{updateTime} 

	,personal_id = #{personalId}


	,sid = #{sid}


	,tid = #{tid}


	,jurisdiction_id = #{jurisdictionId}


	,t_json = #{tJson}


	,create_time = #{createTime}


	,creator = #{creator}


	,updator = #{updator}

WHERE 1 = 1

	AND personal_id = #{personalId}


	AND sid = #{sid}


	AND tid = #{tid}


	AND jurisdiction_id = #{jurisdictionId}


	AND t_json = #{tJson}


	AND create_time = #{createTime}


	AND creator = #{creator}


	AND update_time = #{updateTime}


	AND updator = #{updator}

