mapper.update:
UPDATE s_grade_label SET update_time = #{updateTime} 

	,grade_id = #{gradeId}


	,sid = #{sid}


	,grade_name = #{gradeName}


	,is_use = #{isUse}


	,create_time = #{createTime}


	,creator = #{creator}


	,updator = #{updator}

WHERE 1 = 1

	AND grade_id = #{gradeId}


	AND sid = #{sid}


	AND grade_name = #{gradeName}


	AND is_use = #{isUse}


	AND create_time = #{createTime}


	AND creator = #{creator}


	AND update_time = #{updateTime}


	AND updator = #{updator}

mapper.select:
SELECT

FROM s_grade_label
WHERE 1 = 1

	AND grade_id = #{gradeId}


	AND sid = #{sid}


	AND grade_name = #{gradeName}


	AND is_use = #{isUse}


	AND create_time = #{createTime}


	AND creator = #{creator}


	AND update_time = #{updateTime}


	AND updator = #{updator}

