mapper.update:
UPDATE s_work_time SET update_time = #{updateTime} 

	,work_id = #{workId}


	,sid = #{sid}


	,work_name = #{workName}


	,is_use = #{isUse}


	,create_time = #{createTime}


	,creator = #{creator}


	,updator = #{updator}

WHERE 1 = 1

	AND work_id = #{workId}


	AND sid = #{sid}


	AND work_name = #{workName}


	AND is_use = #{isUse}


	AND create_time = #{createTime}


	AND creator = #{creator}


	AND update_time = #{updateTime}


	AND updator = #{updator}

mapper.select:
SELECT

FROM s_work_time
WHERE 1 = 1

	AND work_id = #{workId}


	AND sid = #{sid}


	AND work_name = #{workName}


	AND is_use = #{isUse}


	AND create_time = #{createTime}


	AND creator = #{creator}


	AND update_time = #{updateTime}


	AND updator = #{updator}

mapper.update:
UPDATE s_work_time SET update_time = #{updateTime} 

	,work_id = #{workId}


	,sid = #{sid}


	,work_name = #{workName}


	,is_use = #{isUse}


	,create_time = #{createTime}


	,creator = #{creator}


	,updator = #{updator}

WHERE 1 = 1

	AND work_id = #{workId}

mapper.delete:
UPDATE s_work_time SET ddd='1', update_time = #{updateTime}, uu2 =
#{uu2} WHERE update_time = #{updateTime}

	AND work_id = #{workId}

mapper.select:
SELECT

FROM s_work_time WHERE 1 = 1

	AND work_id = #{workId}
