mapper.update:
UPDATE u_parent SET update_time = #{updateTime} 

    ,parent_id = #{parentId}


    ,sid = #{sid}


    ,uid = #{uid}


    ,parent_name = #{parentName}


    ,phone = #{phone}


    ,work_unit = #{workUnit}


    ,position = #{position}


    ,method = #{method}


    ,openid = #{openid}


    ,wechatname = #{wechatname}


    ,wechathead = #{wechathead}


    ,city = #{city}


    ,gender = #{gender}


    ,status = #{status}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND parent_id = #{parentId}
    
    
        AND sid = #{sid}
    
    
        AND uid = #{uid}
    
    
        AND parent_name = #{parentName}
    
    
        AND phone = #{phone}
    
    
        AND work_unit = #{workUnit}
    
    
        AND position = #{position}
    
    
        AND method = #{method}
    
    
        AND openid = #{openid}
    
    
        AND wechatname = #{wechatname}
    
    
        AND wechathead = #{wechathead}
    
    
        AND city = #{city}
    
    
        AND gender = #{gender}
    
    
        AND status = #{status}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

