mapper.select:
SELECT a.student_id, a.student_name FROM u_student a, class_student b
a.student_id = b.student_id
AND b.cid = #{cid}

SELECT c.cid, c.class_name, e.teacher_name AS leader_name, a.number, a.student_id, a.student_name, a.phone, g.parent_id, g.parent_name, a.is_use FROM u_student a LEFT JOIN class_student b ON a.student_id = b.student_id LEFT JOIN class c ON b.cid = c.cid LEFT JOIN class_teacher d ON c.cid = d.cid AND d.is_leader = 1 LEFT JOIN u_teacher e ON d.tid = e.tid LEFT JOIN u_student_parent f ON a.student_id = f.student_id AND f.is_default = 1 LEFT JOIN u_parent g ON f.parent_id = g.parent_id WHERE 1 = 1
AND a.student_id = #{studentId}
AND a.sid = #{sid} AND a.number = #{number}

SELECT c.cid, c.class_name, e.teacher_name AS leader_name, a.number, a.student_id, a.student_name, a.phone, g.parent_id, g.parent_name, a.is_use, h.grade_name FROM u_student a LEFT JOIN class_student b ON a.student_id = b.student_id LEFT JOIN class c ON b.cid = c.cid LEFT JOIN class_teacher d ON c.cid = d.cid AND d.is_leader = 1 LEFT JOIN u_teacher e ON d.tid = e.tid LEFT JOIN u_student_parent f ON a.student_id = f.student_id AND f.is_default = 1 LEFT JOIN u_parent g ON f.parent_id = g.parent_id LEFT JOIN s_grade_label h ON c.grade_id = h.grade_id WHERE 1 = 1
AND a.student_id = #{studentId}
AND a.sid = #{sid} AND a.number = #{number}

SELECT a.student_id, a.number, a.student_name FROM u_student a, class_student b, class c, s_term d
a.student_id = b.student_id AND b.cid = c.cid AND c.term_id = d.term_id
AND c.term_id = #{termId}
AND c.cid = #{cid}
AND c.sid = #{sid}
