mapper.update:
UPDATE mep_score SET update_time = #{updateTime} 

    ,mep_score_id = #{mepScoreId}


    ,term_id = #{termId}


    ,cid = #{cid}


    ,student_id = #{studentId}


    ,mep_item_id = #{mepItemId}


    ,scroe_order = #{scroeOrder}


    ,scroe = #{scroe}


    ,score_time = #{scoreTime}


    ,tid = #{tid}


    ,score_pic = #{scorePic}


    ,score_content = #{scoreContent}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND mep_score_id = #{mepScoreId}
    
    
        AND term_id = #{termId}
    
    
        AND cid = #{cid}
    
    
        AND student_id = #{studentId}
    
    
        AND mep_item_id = #{mepItemId}
    
    
        AND scroe_order = #{scroeOrder}
    
    
        AND scroe = #{scroe}
    
    
        AND score_time = #{scoreTime}
    
    
        AND tid = #{tid}
    
    
        AND score_pic = #{scorePic}
    
    
        AND score_content = #{scoreContent}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

