mapper.select:
SELECT c.student_id, c.student_name, c.number, c.totalScore, CASE WHEN @rowtotal = c.totalScore THEN @ranking WHEN @rowtotal := c.totalScore THEN @ranking :=@ranking + 1 WHEN @rowtotal = 0 THEN @ranking :=@ranking + 1 END AS ranking FROM ( SELECT a.student_id, b.student_name, b.number, Sum(a.scroe) AS totalScore FROM mep_score AS a LEFT JOIN u_student AS b ON a.student_id = b.student_id WHERE 1 = 1
AND a.term_id = #{termId}
AND a.cid = #{cid}
GROUP BY a.student_id ORDER BY totalScore DESC ) AS c, ( SELECT @ranking := 0 ,@rowtotal := NULL ) r

SELECT c.student_id, c.student_name, c.number, c.totalScore, CASE WHEN @rowtotal = c.totalScore THEN @ranking WHEN @rowtotal := c.totalScore THEN @ranking :=@ranking + 1 WHEN @rowtotal = 0 THEN @ranking :=@ranking + 1 END AS ranking FROM ( SELECT a.student_id, b.student_name, b.number, Sum(a.scroe) AS totalScore FROM mep_score AS a LEFT JOIN u_student AS b ON a.student_id = b.student_id WHERE 1 = 1
AND a.term_id = #{termId}
AND a.cid = #{cid}
GROUP BY a.student_id ORDER BY totalScore ASC ) AS c, ( SELECT @ranking := 0 ,@rowtotal := NULL ) r

SELECT d.student_id, d.student_name, d.number, d.totalScore, d.ranking FROM ( SELECT c.student_id, c.student_name, c.number, c.totalScore, CASE WHEN @rowtotal = c.totalScore THEN @ranking WHEN @rowtotal := c.totalScore THEN @ranking :=@ranking + 1 WHEN @rowtotal = 0 THEN @ranking :=@ranking + 1 END AS ranking FROM ( SELECT a.student_id, b.student_name, b.number, Sum(a.scroe) AS totalScore FROM mep_score AS a LEFT JOIN u_student AS b ON a.student_id = b.student_id WHERE 1 = 1
AND a.term_id = #{termId}
AND a.cid = #{cid}
GROUP BY a.student_id ORDER BY totalScore DESC
) AS c, ( SELECT @ranking := 0 ,@rowtotal := NULL ) r ) AS d WHERE 1 = 1
AND d.student_id = #{studentId}

SELECT a.mep_score_id, b.mep_item_name, a.scroe, b.mep_item_explain, a.score_time, c.teacher_name FROM mep_score AS a LEFT JOIN mep_item AS b ON a.mep_item_id = b.mep_item_id LEFT JOIN u_teacher AS c ON a.tid = c.tid WHERE 1 = 1
AND a.student_id = #{studentId}
ORDER BY a.create_time DESC

SELECT a.mep_score_id, b.mep_item_name, a.scroe, b.mep_item_explain, a.score_time, c.teacher_name FROM mep_score AS a LEFT JOIN mep_item AS b ON a.mep_item_id = b.mep_item_id LEFT JOIN u_teacher AS c ON a.tid = c.tid WHERE 1 = 1
AND a.student_id = #{studentId}
AND a.mep_item_id = #{mepItemId}
AND a.create_time BETWEEN #{startTime} and #{endTime}
ORDER BY a.create_time DESC

SELECT e.student_id, e.student_name, e.number, e.totalScore, e.ranking FROM ( SELECT d.student_id, d.student_name, d.number, d.totalScore, CASE WHEN @rowtotal = d.totalScore THEN @ranking WHEN @rowtotal := d.totalScore THEN @ranking :=@ranking + 1 WHEN @rowtotal = 0 THEN @ranking :=@ranking + 1 END AS ranking FROM ( SELECT a.student_id, b.student_name, b.number, Sum(a.scroe) AS totalScore FROM mep_score AS a LEFT JOIN u_student AS b ON a.student_id = b.student_id LEFT JOIN class AS c ON a.cid = c.cid WHERE 1 = 1
AND a.term_id = #{termId}
AND c.grade_id = #{gradeId}
AND a.cid = #{cid}
GROUP BY a.student_id ORDER BY totalScore DESC ) AS d, ( SELECT @ranking := 0 ,@rowtotal := NULL ) r ) AS e WHERE 1 = 1
AND e.student_name LIKE '%${studentName}%'
