mapper.update:
UPDATE alarm_rule SET update_time = #{updateTime} 

    ,alarmrule_id = #{alarmruleId}


    ,sid = #{sid}


    ,alarmrule_name = #{alarmruleName}


    ,device_id = #{deviceId}


    ,start_time = #{startTime}


    ,end_time = #{endTime}


    ,flag = #{flag}


    ,time_type = #{timeType}


    ,time_repeat = #{timeRepeat}


    ,time_date = #{timeDate}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND alarmrule_id = #{alarmruleId}
    
    
        AND sid = #{sid}
    
    
        AND alarmrule_name = #{alarmruleName}
    
    
        AND device_id = #{deviceId}
    
    
        AND start_time = #{startTime}
    
    
        AND end_time = #{endTime}
    
    
        AND flag = #{flag}
    
    
        AND time_type = #{timeType}
    
    
        AND time_repeat = #{timeRepeat}
    
    
        AND time_date = #{timeDate}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

