mapper.update:
UPDATE class SET update_time = #{updateTime} 

    ,cid = #{cid}


    ,sid = #{sid}


    ,class_name = #{className}


    ,year = #{year}


    ,cnum = #{cnum}


    ,term_id = #{termId}


    ,grade_id = #{gradeId}


    ,work_id = #{workId}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND cid = #{cid}
    
    
        AND sid = #{sid}
    
    
        AND class_name = #{className}
    
    
        AND year = #{year}
    
    
        AND cnum = #{cnum}
    
    
        AND term_id = #{termId}
    
    
        AND grade_id = #{gradeId}
    
    
        AND work_id = #{workId}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

