mapper.select:
SELECT a.cid, a.class_name, a.work_id, b.work_name, a.create_time FROM class a left join s_work_time b on a.work_id = b.work_id
and a.cid = #{cid}
and a.term_id = #{termId}
and a.grade_id = #{gradeId}

SELECT a.cid, a.class_name, b.term_name, c.grade_name, a.create_time, f.tid, f.teacher_name FROM class a LEFT JOIN s_term b ON a.term_id = b.term_id LEFT JOIN s_grade_label c ON a.grade_id = c.grade_id LEFT JOIN class_teacher e ON a.cid = e.cid and e.is_leader = 1 LEFT JOIN u_teacher f ON e.tid = f.tid
and a.cid = #{cid}
and a.term_id = #{termId}
and a.grade_id = #{gradeId}
and f.tid = #{tid}

SELECT DISTINCT a.cid, a.class_name, b.term_name, COUNT(f.student_id) AS student_count, COUNT(g.course_id1) AS course_count FROM class a LEFT JOIN s_term b ON a.term_id = b.term_id LEFT JOIN s_grade_label c ON a.grade_id = c.grade_id LEFT JOIN class_teacher e ON a.cid = e.cid LEFT JOIN class_student f ON a.cid = f.cid LEFT JOIN class_course g ON a.cid = g.cid
and a.cid = #{cid}
and a.term_id = #{termId}
and b.is_default = 1
and a.grade_id = #{gradeId}
and e.tid = #{tid}
GROUP BY f.student_id, g.course_id1, a.cid, a.class_name, b.term_name

SELECT cid, class_name FROM class WHERE 1 = 1
AND instr(#{newsClasser}, concat(cid, ';')) > 0
AND sid = #{sid}
