mapper.update:
UPDATE class_alarm SET update_time = #{updateTime} 

    ,did = #{did}


    ,cid = #{cid}


    ,sid = #{sid}


    ,alarmrule_id = #{alarmruleId}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND did = #{did}
    
    
        AND cid = #{cid}
    
    
        AND sid = #{sid}
    
    
        AND alarmrule_id = #{alarmruleId}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

mapper.select:
SELECT  FROM class_alarm
WHERE 1 = 1 

    AND did = #{did}


    AND cid = #{cid}


    AND sid = #{sid}


    AND alarmrule_id = #{alarmruleId}


    AND is_use = #{isUse}


    AND create_time = #{createTime}


    AND creator = #{creator}


    AND update_time = #{updateTime}


    AND updator = #{updator}

mapper.update:
UPDATE class_alarm SET update_time = #{updateTime} 

    ,did = #{did}


    ,cid = #{cid}


    ,sid = #{sid}


    ,alarmrule_id = #{alarmruleId}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND cid = #{cid}

mapper.delete:
        DELETE FROM class_alarm 
        	
	            
	                AND did = #{did}
	            
	            
	            	and alarmrule_id = #{alarmruleId}
