mapper.update:
UPDATE log_security SET update_time = #{updateTime} 

    ,id = #{id}


    ,sid = #{sid}


    ,term_id = #{termId}


    ,cid = #{cid}


    ,student_id = #{studentId}


    ,rfid = #{rfid}


    ,device_id = #{deviceId}


    ,mark_time = #{markTime}


    ,flag = #{flag}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND id = #{id}
    
    
        AND sid = #{sid}
    
    
        AND term_id = #{termId}
    
    
        AND cid = #{cid}
    
    
        AND student_id = #{studentId}
    
    
        AND rfid = #{rfid}
    
    
        AND device_id = #{deviceId}
    
    
        AND mark_time = #{markTime}
    
    
        AND flag = #{flag}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

