mapper.update:
UPDATE mec_score SET update_time = #{updateTime} 

    ,mec_score_id = #{mecScoreId}


    ,term_id = #{termId}


    ,cid = #{cid}


    ,mec_item_id = #{mecItemId}


    ,scroe_order = #{scroeOrder}


    ,scroe = #{scroe}


    ,score_time = #{scoreTime}


    ,tid = #{tid}


    ,score_pic = #{scorePic}


    ,score_content = #{scoreContent}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND mec_score_id = #{mecScoreId}
    
    
        AND term_id = #{termId}
    
    
        AND cid = #{cid}
    
    
        AND mec_item_id = #{mecItemId}
    
    
        AND scroe_order = #{scroeOrder}
    
    
        AND scroe = #{scroe}
    
    
        AND score_time = #{scoreTime}
    
    
        AND tid = #{tid}
    
    
        AND score_pic = #{scorePic}
    
    
        AND score_content = #{scoreContent}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

