mapper.select:
SELECT d.cid, d.class_name, d.totalScore, CASE WHEN @rowtotal = d.totalScore THEN @ranking WHEN @rowtotal := d.totalScore THEN @ranking :=@ranking + 1 WHEN @rowtotal = 0 THEN @ranking :=@ranking + 1 END AS ranking FROM ( SELECT a.cid, CONCAT(c.grade_name, b.class_name) AS class_name, SUM(a.scroe) AS totalScore FROM mep_score AS a LEFT JOIN class AS b ON a.cid = b.cid LEFT JOIN s_grade_label AS c ON b.grade_id = c.grade_id WHERE 1 = 1
AND a.term_id = #{termId}
GROUP BY a.cid ORDER BY totalScore DESC ) AS d, ( SELECT @ranking := 0 ,@rowtotal := NULL ) r

SELECT d.cid, d.class_name, d.totalScore, CASE WHEN @rowtotal = d.totalScore THEN @ranking WHEN @rowtotal := d.totalScore THEN @ranking :=@ranking + 1 WHEN @rowtotal = 0 THEN @ranking :=@ranking + 1 END AS ranking FROM ( SELECT a.cid, CONCAT(c.grade_name, b.class_name) AS class_name, SUM(a.scroe) AS totalScore FROM mep_score AS a LEFT JOIN class AS b ON a.cid = b.cid LEFT JOIN s_grade_label AS c ON b.grade_id = c.grade_id WHERE 1 = 1
AND a.term_id = #{termId}
GROUP BY a.cid ORDER BY totalScore ASC ) AS d, ( SELECT @ranking := 0 ,@rowtotal := NULL ) r

SELECT d.cid, d.class_name, d.totalScore, CASE WHEN @rowtotal = d.totalScore THEN @ranking WHEN @rowtotal := d.totalScore THEN @ranking :=@ranking + 1 WHEN @rowtotal = 0 THEN @ranking :=@ranking + 1 END AS ranking FROM ( SELECT a.cid, CONCAT(c.grade_name, b.class_name) AS class_name, SUM(a.scroe) AS totalScore FROM mep_score AS a LEFT JOIN class AS b ON a.cid = b.cid LEFT JOIN s_grade_label AS c ON b.grade_id = c.grade_id WHERE 1 = 1
AND a.term_id = #{termId}
AND a.cid = #{cid}
GROUP BY a.cid
) AS d, ( SELECT @ranking := 0 ,@rowtotal := NULL ) r

SELECT a.mec_score_id, a.mec_item_id, b.mec_item_name, a.scroe, c.teacher_name, b.mec_item_explain, a.create_time FROM mec_score AS a LEFT JOIN mec_item AS b ON a.mec_item_id = b.mec_item_id LEFT JOIN u_teacher AS c ON a.tid = c.tid WHERE 1 = 1
AND a.term_id = #{termId}
AND a.cid = #{cid}
ORDER BY a.create_time DESC

SELECT a.mec_score_id, a.mec_item_id, b.mec_item_name, a.scroe, c.teacher_name, b.mec_item_explain, a.create_time FROM mec_score AS a LEFT JOIN mec_item AS b ON a.mec_item_id = b.mec_item_id LEFT JOIN u_teacher AS c ON a.tid = c.tid WHERE 1 = 1
AND a.term_id = #{termId}
AND a.cid = #{cid}
AND b.mec_item_id = #{mecItemId}
AND a.create_time BETWEEN #{startTime} and #{endTime}
ORDER BY a.create_time DESC

SELECT e.cid, e.class_name, e.totalScore, e.ranking FROM ( SELECT d.cid, d.class_name, d.totalScore, CASE WHEN @rowtotal = d.totalScore THEN @ranking WHEN @rowtotal := d.totalScore THEN @ranking :=@ranking + 1 WHEN @rowtotal = 0 THEN @ranking :=@ranking + 1 END AS ranking FROM ( SELECT a.cid, CONCAT(c.grade_name, b.class_name) AS class_name, SUM(a.scroe) AS totalScore FROM mep_score AS a LEFT JOIN class AS b ON a.cid = b.cid LEFT JOIN s_grade_label AS c ON b.grade_id = c.grade_id WHERE 1 = 1
AND a.term_id = #{termId}
AND b.grade_id = #{gradeId}
GROUP BY a.cid ORDER BY totalScore DESC ) AS d, ( SELECT @ranking := 0 ,@rowtotal := NULL ) r ) AS e WHERE 1 = 1
AND e.cid = #{cid}

SELECT e.cid, e.class_name, e.totalScore, e.ranking FROM ( SELECT d.cid, d.class_name, d.totalScore, CASE WHEN @rowtotal = d.totalScore THEN @ranking WHEN @rowtotal := d.totalScore THEN @ranking :=@ranking + 1 WHEN @rowtotal = 0 THEN @ranking :=@ranking + 1 END AS ranking FROM ( SELECT a.cid, CONCAT(c.grade_name, b.class_name) AS class_name, SUM(a.scroe) AS totalScore FROM mep_score AS a LEFT JOIN class AS b ON a.cid = b.cid LEFT JOIN s_grade_label AS c ON b.grade_id = c.grade_id WHERE 1 = 1
AND a.term_id = #{termId}
AND b.grade_id = #{gradeId}
GROUP BY a.cid ORDER BY totalScore ASC ) AS d, ( SELECT @ranking := 0 ,@rowtotal := NULL ) r ) AS e WHERE 1 = 1
AND e.cid = #{cid}
