mapper.update:
UPDATE oa_examine_information SET update_time = #{updateTime} 

    ,approval_information_id = #{approvalInformationId}


    ,sid = #{sid}


    ,type = #{type}


    ,oatags_id = #{oatagsId}


    ,tid = #{tid}


    ,content = #{content}


    ,result = #{result}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND approval_information_id = #{approvalInformationId}
    
    
        AND sid = #{sid}
    
    
        AND type = #{type}
    
    
        AND oatags_id = #{oatagsId}
    
    
        AND tid = #{tid}
    
    
        AND content = #{content}
    
    
        AND result = #{result}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

mapper.select:
