mapper.update:
UPDATE s_course SET update_time = #{updateTime} 

	,course_id = #{courseId}


	,sid = #{sid}


	,course_name = #{courseName}


	,is_use = #{isUse}


	,create_time = #{createTime}


	,creator = #{creator}


	,updator = #{updator}

WHERE 1 = 1

	AND course_id = #{courseId}


	AND sid = #{sid}


	AND course_name = #{courseName}


	AND is_use = #{isUse}


	AND create_time = #{createTime}


	AND creator = #{creator}


	AND update_time = #{updateTime}


	AND updator = #{updator}

mapper.select:
SELECT

FROM s_course
WHERE 1 = 1

	AND course_id = #{courseId}


	AND sid = #{sid}


	AND course_name = #{courseName}


	AND is_use = #{isUse}


	AND create_time = #{createTime}


	AND creator = #{creator}


	AND update_time = #{updateTime}


	AND updator = #{updator}

mapper.update:
UPDATE s_course SET update_time = #{updateTime} 

	,course_id = #{courseId}


	,sid = #{sid}


	,course_name = #{courseName}


	,is_use = #{isUse}


	,create_time = #{createTime}


	,creator = #{creator}


