mapper.delete:
UPDATE s_course SET ddd='1', update_time = #{updateTime}, uu2 = #{uu2}
WHERE update_time = #{updateTime}

	AND course_id = #{courseId}

mapper.select:
SELECT

FROM s_course WHERE 1 = 1

	AND course_id = #{courseId}
