mapper.update:
UPDATE s_term SET update_time = #{updateTime} 

    ,term_id = #{termId}


    ,sid = #{sid}


    ,term_name = #{termName}


    ,start_time = #{startTime}


    ,end_time = #{endTime}


    ,is_default = #{isDefault}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND term_id = #{termId}
    
    
        AND sid = #{sid}
    
    
        AND term_name = #{termName}
    
    
        AND start_time = #{startTime}
    
    
        AND end_time = #{endTime}
    
    
        AND is_default = #{isDefault}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

mapper.select:
SELECT  FROM s_term
WHERE 1 = 1 

    AND term_id = #{termId}


    AND sid = #{sid}


    AND term_name = #{termName}


    AND start_time = #{startTime}


    AND end_time = #{endTime}


    AND is_default = #{isDefault}


    AND is_use = #{isUse}


    AND create_time = #{createTime}


    AND creator = #{creator}


    AND update_time = #{updateTime}


    AND updator = #{updator}

