mapper.update:
UPDATE student_rfid SET update_time = #{updateTime} 

    ,id = #{id}


    ,sid = #{sid}


    ,student_id = #{studentId}


    ,rfid = #{rfid}


    ,is_effective = #{isEffective}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND id = #{id}
    
    
        AND sid = #{sid}
    
    
        AND student_id = #{studentId}
    
    
        AND rfid = #{rfid}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}

mapper.select:
SELECT  FROM student_rfid
WHERE 1 = 1 

    AND id = #{id}


    AND sid = #{sid}


    AND student_id = #{studentId}


    AND rfid = #{rfid}


    AND is_effective = #{isEffective}


    AND is_use = #{isUse}


    AND create_time = #{createTime}


    AND creator = #{creator}


    AND update_time = #{updateTime}


    AND updator = #{updator}

