mapper.update:
UPDATE task SET update_time = #{updateTime} 

    ,task_id = #{taskId}


    ,sid = #{sid}


    ,term_id = #{termId}


    ,course_id = #{courseId}


    ,task_name = #{taskName}


    ,content = #{content}


    ,video = #{video}


    ,pic = #{pic}


    ,task_classer = #{taskClasser}


    ,start_time = #{startTime}


    ,end_time = #{endTime}


    ,is_top = #{isTop}


    ,publish_time = #{publishTime}


    ,is_audit = #{isAudit}


    ,num = #{num}


    ,author = #{author}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND task_id = #{taskId}
    
    
        AND sid = #{sid}
    
    
        AND term_id = #{termId}
    
    
        AND course_id = #{courseId}
    
    
        AND task_name = #{taskName}
    
    
        AND content = #{content}
    
    
        AND video = #{video}
    
    
        AND pic = #{pic}
    
    
        AND task_classer = #{taskClasser}
    
    
        AND start_time = #{startTime}
    
    
        AND end_time = #{endTime}
    
    
        AND is_top = #{isTop}
    
    
        AND publish_time = #{publishTime}
    
    
        AND is_audit = #{isAudit}
    
    
        AND num = #{num}
    
    
        AND author = #{author}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

