mapper.select:
SELECT
a.task_id,a.task_name,a.is_top,a.author,a.course_id,b.course_name,c.teacher_name authorName ,a.publishTime
from task a,s_course b, s_grade_lable c,u_teacger c
where a.course_id = b.course_id and a.author=c.teacher_name
and instr(a.task_classer, concat(concat(#{cid}, ';') > 0
  
        AND sid = #{sid}
  
 
        AND a.term_id = #{termId}
 
  
        AND author = #{author}
  
  order by a.publish_time desc

SELECT
a.task_id,a.task_name,a.is_top,a.author,a.course_id,b.course_name,c.teacher_name authorName ,a.publishTime
from task a,s_course b, s_grade_lable c,u_teacger c
where a.course_id = b.course_id and a.author=c.teacher_name
  
        AND sid = #{sid}
  
 
        AND a.term_id = #{termId}
 
  
        AND author = #{author}
  
  order by a.publish_time desc

SELECT
a.*,b.course_name,c.teacher_name authorName
from task a,s_course b, s_grade_lable c,u_teacger c
where a.course_id = b.course_id and a.author=c.teacher_name
  
        AND task_id = #{taskId}
  
  
        AND sid = #{sid}
  
 
        AND a.term_id = #{termId}
 
  
        AND author = #{author}
  
  
       and instr(a.task_classer, concat(concat(#{cid}, ';') > 0

SELECT
a.task_id,a.task_name,a.is_top,a.author,a.course_id,b.course_name,c.teacher_name authorName ,a.publishTime
from task a,s_course b, s_grade_lable c,u_teacger c
where a.course_id = b.course_id and a.author=c.teacher_name
  
        AND sid = #{sid}
  
 
        AND a.term_id = #{termId}
 
 
        AND task_name like '%#{taskName}%'
    
  
        AND author = #{author}
  
  
       and instr(a.task_classer, concat(concat(#{cid}, ';') > 0
 
  order by a.publish_time desc
