mapper.update:
UPDATE u_student SET update_time = #{updateTime} 

    ,student_id = #{studentId}


    ,uid = #{uid}


    ,sid = #{sid}


    ,number = #{number}


    ,student_name = #{studentName}


    ,phone = #{phone}


    ,method = #{method}


    ,openid = #{openid}


    ,wechatname = #{wechatname}


    ,wechathead = #{wechathead}


    ,city = #{city}


    ,gender = #{gender}


    ,status = #{status}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND student_id = #{studentId}
    
    
        AND uid = #{uid}
    
    
        AND sid = #{sid}
    
    
        AND number = #{number}
    
    
        AND student_name = #{studentName}
    
    
        AND phone = #{phone}
    
    
        AND method = #{method}
    
    
        AND openid = #{openid}
    
    
        AND wechatname = #{wechatname}
    
    
        AND wechathead = #{wechathead}
    
    
        AND city = #{city}
    
    
        AND gender = #{gender}
    
    
        AND status = #{status}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

