mapper.update:
UPDATE u_student_parent SET update_time = #{updateTime} 

    ,id = #{id}


    ,parent_id = #{parentId}


    ,role = #{role}


    ,sid = #{sid}


    ,student_id = #{studentId}


    ,is_default = #{isDefault}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND id = #{id}
    
    
        AND parent_id = #{parentId}
    
    
        AND role = #{role}
    
    
        AND sid = #{sid}
    
    
        AND student_id = #{studentId}
    
    
        AND is_default = #{isDefault}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

