mapper.update:
UPDATE u_teacher SET update_time = #{updateTime} 

    ,tid = #{tid}


    ,uid = #{uid}


    ,sid = #{sid}


    ,teacher_name = #{teacherName}


    ,phone = #{phone}


    ,email = #{email}


    ,id_type = #{idType}


    ,openid = #{openid}


    ,wechatname = #{wechatname}


    ,wechathead = #{wechathead}


    ,city = #{city}


    ,gender = #{gender}


    ,status = #{status}


    ,is_use = #{isUse}


    ,create_time = #{createTime}


    ,creator = #{creator}


    ,updator = #{updator}

WHERE 1 = 1 
    
        AND tid = #{tid}
    
    
        AND uid = #{uid}
    
    
        AND sid = #{sid}
    
    
        AND teacher_name = #{teacherName}
    
    
        AND phone = #{phone}
    
    
        AND email = #{email}
    
    
        AND id_type = #{idType}
    
    
        AND openid = #{openid}
    
    
        AND wechatname = #{wechatname}
    
    
        AND wechathead = #{wechathead}
    
    
        AND city = #{city}
    
    
        AND gender = #{gender}
    
    
        AND status = #{status}
    
    
        AND is_use = #{isUse}
    
    
        AND create_time = #{createTime}
    
    
        AND creator = #{creator}
    
    
        AND update_time = #{updateTime}
    
    
        AND updator = #{updator}

